import{Command as e}from"commander";import a from"fast-glob";import n from"esbuild";import m from"fs";const t=new e;t.name("playform").description("CLI to some JavaScript build scripts").version("0.0.1"),t.command("build").description("Build scripts").argument("<scripts>","Scripts to build").action(async i=>{const o=await a(i),r="./dist";await n.build({entryPoints:o,format:"esm",minify:!0,outdir:r,platform:"node",target:"node14",write:!0,plugins:[{name:"clean-dist",setup(s){console.log("clean-dist"),s.onStart(async()=>{await m.promises.rm(r,{recursive:!0})})}}]})}),t.parse();
